import React from 'react';
import Sketch from "react-p5";
import p5Types from "p5";
import './Dashboard.css';

export default function Dashboard()
{
  const   width = 1300;
	const   height = 600;
  const Xb = width / 20;

  let Board = new Array(1300).fill(new Array(600).fill(0));

  const setup = (p5: p5Types, canvasParentRef: Element) => {
		p5.createCanvas(width, height).parent(canvasParentRef);
    p5.stroke(50); // Set line drawing color to white
    p5.frameRate(30);

		p5.background('#222222')
    p5.square(0, 0, 20);
    // p5.describe('white square with black outline and round edges of different radii');


    for (let i = 0; i < 1300; i+=20) {
      p5.line(i, 0, i, 1300);
    }
    for (let i = 0; i < 600; i+=20) {
      p5.line(0, i, 1300, i);
    }
    p5.fill(100);
    Board[0][0] = 2;

    for (let i = 0; i < 1300; i+=20) 
    {
      for (let j = 0; j < 600; j+=20) 
      {
        console.log(Board[i][j]);
      }
      // console.log("\n");
    }

  };

	const draw = (p5: p5Types) => {


    p5.background('#222222')
    // for (let i = 0; i < 1300; i+=20) {
    //   p5.line(i, 0, i, 1300);
    // }
    // for (let i = 0; i < 600; i+=20) {
    //   p5.line(0, i, 1300, i);
    // }

    for (let i = 0; i < 1300; i+=20) 
    {
      for (let j = 0; j < 600; j+=20) 
      {
        if (Board[i][j] === 1)
        {
          p5.fill("red");
          p5.square(i, j, 20);
          p5.noFill();
        }
        else if (Board[i][j] === 2)
        {
          p5.fill("blue");
          p5.square(i, j, 20);
          p5.noFill();
        }
        else 
        {
          p5.fill("white");
          p5.square(i, j, 20);
          p5.noFill();
        }

      }
    }

    // // p5.fill(100);
    // p5.text(p5.key, 33, 65);

    // if (p5.key === 's') {




    //   p5.fill("red");
    //   let x = (p5.mouseX / 20) * 20;
    //   let y = (p5.mouseY / 20) * 20;

    //   console.log("hello " , x, y);

    //   p5.square(x, y, 20);

    //   if (p5.mouseIsPressed === true && p5.mouseButton === p5.LEFT)
    //   {
    //     p5.fill("bleu");
    //     p5.square(x, y, 20);
    //   }
    // }


    // console.log(p5.key)

	};

  return(
    <div className='board'>
      <Sketch setup={setup} draw={draw} />
      <div className='board__info'>
        <div>Dashboard
          </div>
        <p>Press 's' to start drawing</p>
      </div>
    </div>
  )
}

